import React, { useState } from "react";
import axios from "axios";

import styled from "styled-components";

import {
  Header,
  AppNameComponent,
  AppIcon,
  SearchComponent,
  SearchInput,
  SearchIcon,
} from "./components/header";

import {
  Container,
  RecipeList,
  Recipes,
  RecipeImage,
  RecipeTitle,
  IngredientsText,
  SeeMoreText,
} from "./components/recipes";

const App = () => {
  const [timeoutId, updateTimeoutId] = useState();

  const APP_ID = "dd3ffea2";

  const APP_KEY = "b4b37afad59e070a14fd1b07d5fdccc3";

  const fetchRecipe = async (searchString) => {
    const APP_ID = "dd3ffea2";

    const APP_KEY = "b4b37afad59e070a14fd1b07d5fdccc3";

    const url = `https://api.edamam.com/api/recipes/v2?type=public&q=${searchString}&app_id=${APP_ID}&app_key=${APP_KEY}`;

    const result = await axios.get(url);

    console.log(result);
  };

  //debouncing to minimize api calls
  const onTextChange = (e) => {
    clearTimeout(timeoutId);
    const timeout = setTimeout(() => fetchRecipe(e.target.value), 500);
    updateTimeoutId(timeout);
  };

  return (
    <Container>
      <Header>
        <AppNameComponent>
          <AppIcon src="hamburger.svg" />
          Recipe Finder
        </AppNameComponent>
        <SearchComponent>
          <SearchIcon src="/search-icon.svg" />
          <SearchInput placeholder="Search recipe..." onChange={onTextChange} />
        </SearchComponent>
      </Header>
      <RecipeList>
        <Recipes>
          <RecipeImage />
          <RecipeTitle>Hello</RecipeTitle>
          <IngredientsText>test test test test test</IngredientsText>
          <SeeMoreText>test test test test test</SeeMoreText>
        </Recipes>
      </RecipeList>
    </Container>
  );
};

export default App;
