import React, { useState } from "react";
import axios from "axios";
import styled from "styled-components";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";

import {
  Header,
  AppNameComponent,
  AppIcon,
  SearchComponent,
  SearchInput,
  SearchIcon,
} from "./components/header";

import {
  RecipeContainer,
  RecipeImage,
  RecipeTitle,
  IngredientsText,
  SeeMoreText,
} from "./components/recipe";

export const RecipeComponent = (props) => {
  const [open, setOpen] = useState(false);
  const { recipeObj } = props;
  return (
    <>
      <Dialog open={open}>
        <DialogTitle id="alert-dialog-slide-title">Ingredients</DialogTitle>
        <DialogContent>
          <table>
            <thead>
              <th>Ingredients</th>
              <th>Weight</th>
            </thead>
            <tbody>
              {recipeObj.ingredients.map((ingredientObj) => (
                <tr>
                  <td>{ingredientObj.text}</td>
                  <td>{ingredientObj.weight}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </DialogContent>
        <DialogActions>
          <IngredientsText onClick={() => window.open(recipeObj.url)}>
            See More
          </IngredientsText>
          <SeeMoreText onClick={() => setOpen("")}>Close</SeeMoreText>
        </DialogActions>
      </Dialog>

      <RecipeContainer>
        <RecipeImage src={recipeObj.image} />
        <RecipeTitle>{recipeObj.label}</RecipeTitle>
        <IngredientsText onClick={() => setOpen(true)}>
          Ingredients
        </IngredientsText>
        <SeeMoreText onClick={() => window.open(recipeObj.url)}>
          See Complete Recipe
        </SeeMoreText>
      </RecipeContainer>
    </>
  );
};

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

export const RecipeListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  gap: 20px;
  justify-content: space-evenly;
`;

const App = () => {
  const [timeoutId, updateTimeoutId] = useState();
  const [recipeList, updateRecipeList] = useState([]);

  const APP_ID = "dd3ffea2";

  const APP_KEY = "b4b37afad59e070a14fd1b07d5fdccc3";

  const fetchRecipe = async (searchString) => {
    const APP_ID = "dd3ffea2";

    const APP_KEY = "b4b37afad59e070a14fd1b07d5fdccc3";

    const url = `https://api.edamam.com/api/recipes/v2?type=public&q=${searchString}&app_id=${APP_ID}&app_key=${APP_KEY}`;

    const result = await axios.get(url);

    updateRecipeList(result.data.hits);
  };

  //debouncing to minimize api calls
  const onTextChange = (e) => {
    clearTimeout(timeoutId);
    const timeout = setTimeout(() => fetchRecipe(e.target.value), 500);
    updateTimeoutId(timeout);
  };

  return (
    <Container>
      <Header>
        <AppNameComponent>
          <AppIcon src="hamburger.svg" />
          Recipe Finder
        </AppNameComponent>
        <SearchComponent>
          <SearchIcon src="/search-icon.svg" />
          <SearchInput placeholder="Search recipe..." onChange={onTextChange} />
        </SearchComponent>
      </Header>

      <RecipeListContainer>
        {recipeList.length &&
          recipeList.map((recipeObj, index) => (
            <RecipeComponent key={index} recipeObj={recipeObj.recipe} />
          ))}
      </RecipeListContainer>
    </Container>
  );
};

export default App;
